import { useEffect } from 'react';
import { getAuth, GoogleAuthProvider, EmailAuthProvider, onAuthStateChanged } from 'firebase/auth'; 
import firebase from "firebase/compat/app";
import * as firebaseui from "firebaseui";
import "firebaseui/dist/firebaseui.css";
import {app} from "../../firebase";
import {useNavigate} from "react-router-dom";
import {auth,db} from "../../firebase";
import { doc, setDoc, getDoc } from "firebase/firestore";

function Login() {
    const navigate = useNavigate();
    useEffect(() => {
        const ui = firebaseui.auth.AuthUI.getInstance() || new firebaseui.auth.AuthUI(getAuth(app));

        ui.start("#firebaseui-auth-container", {
            signInOptions: [
                {
                    provider: GoogleAuthProvider.PROVIDER_ID,
                    customParameters: {
                        prompt: "select_account",
                    },
                },
                {
                    provider: EmailAuthProvider.PROVIDER_ID,
                },
            ],
            signInSuccessUrl: "/inputprofile",
        });

        console.log('Setting up onAuthStateChanged listener...');
        const unsubscribe = onAuthStateChanged(auth, async (user) => {
            console.log('Auth state changed, user:', user);
            if (user) {
                console.log('User is logged in:', user);
                // Check if the user document exists in Firestore
                const userRef = doc(db, "users", user.uid);
                const docSnap = await getDoc(userRef);

                if (!docSnap.exists()) {
                    console.log('No user is logged in');
                    // If document doesn't exist, create it and navigate to the profile page
                    await setDoc(userRef, {
                        email: user.email,
                        profileComplete: false,
                        createdAt: new Date(),
                    });
                    navigate("/inputprofile");
                } else {
                    // If document exists, check profile completion
                    const userData = docSnap.data();
                    if (userData.profileComplete) {
                        navigate("/home");
                    } else {
                        navigate("/inputprofile");
                    }
                }
            } else {
                // If user is not logged in, just stay on login page
                navigate("/login");
            }
        });


    return () => {
        unsubscribe(); 
        ui.reset();
    };
}, [navigate]);





return <div id="firebaseui-auth-container"></div>




}

export default Login
