import React from 'react';
import './inputprofile.css';
import { useNavigate } from 'react-router-dom';
import { useState } from 'react';
import { auth, db } from "../../firebase";
import { doc, updateDoc } from "firebase/firestore";


function InputProfile () {
    const [profileData, setProfileData] = useState({
        name: "",
        birthCity: "",
        birthState: "",
        birthDate: "",
        birthTime: "",
    });

    const navigate = useNavigate();
    const user = auth.currentUser;

    const handleInputChange = (e) => {
        const { name, value } = e.target;
        setProfileData((prevState) => ({
            ...prevState,
            [name]: value,
        }));
    };

    const handleSubmit = async(e) => {
        e.preventDefault();

        //Make sure all fields are filled in
        if (!profileData.name || !profileData.birthCity ||profileData.birthState ||profileData.birthDate || profileData.birthTime) {
            alert("Please fill in all fields.");
            return;
        }

        try {
            //Update user doc in Firestore with their completed profile.
            const userRef = doc(db, "users", user.uid);
            await updateDoc(userRef, {
                name: profileData.name,
                birthCity: profileData.birthCity,
                birthState: profileData.birthState,
                birthDate: profileData.birthDate,
                birthTime: profileData.birthTime,
                profileComplete: true,

            });

            navigate('/home');
        } catch (error) {
            console.error("Error update profile: ", error);
        }
    }


    return (
        <div className = "input-profile">
            <form onSubmit = {handleSubmit}>
                <div>
                    <label htmlFor = "name"></label>
                </div>
            </form>
        </div>
    )



}

export default InputProfile;
