import { useEffect } from 'react';
import { getAuth, GoogleAuthProvider, EmailAuthProvider, onAuthStateChanged } from 'firebase/auth'; 
import firebase from "firebase/compat/app";
import * as firebaseui from "firebaseui";
import "firebaseui/dist/firebaseui.css";

import {useNavigate} from "react-router-dom";
import {auth,db} from "../../firebase";
import { doc, setDoc, getDoc } from "firebase/firestore";

function Login() {
    const navigate = useNavigate();
    useEffect(() => {
        //Initilize FirebaseUI Auth instance
        const ui = 
            firebaseui.auth.AuthUI.getInstance() ||
            new firebaseui.auth.AuthUI(getAuth(app));

    ui.start("#firebaseui-auth-container", {
        signInOptions: [
            {
                provider: GoogleAuthProvider.PROVIDER_ID,
                customParameters: {
                  prompt: "select_account",
                },
              },
            {
                provider: EmailAuthProvider.PROVIDER_ID
            },
        ],
        signInSuccessUrl: "/inputprofile",
    });



    //Check if user has logged in before
    const unsubscribe = onAuthStateChanged(auth, async(user) => {
        if (user) {
            //If user exists, check if they've completed their profile.
            const userRef = doc(db, "users", user.uid);
            const docSnap = await getDoc(userRef);

            if (!docSnap.exists()) {
                //If user document doesn't exist (meaning they haven't created an account) then create it.
                await setDoc(userRef, {
                    email: user.email,
                    profileComplete: false, //This then changes once then create profile in Input Profile page
                    createdAt: new Date(),
                });
                navigate("/inputprofile")
            } else {
                //If user document exists, then check profile completion.
                const userData = docSnap.data();
                if (userData.profileComplete) {
                    navigate("/home");
                } else {
                    navigate("/inputprofile");
                }
            }
        }
    });



    return () => {
        unsubscribe(); 
        ui.reset();
    };
}, [navigate]);





return <div id="firebaseui-auth-container"></div>




}

export default Login
