import React from 'react';
import './inputprofile.css';
import { useNavigate } from 'react-router-dom';
import { useState } from 'react';
import { auth, db } from "../../firebase";
import { doc, updateDoc } from "firebase/firestore";


function InputProfile () {
    const [profileData, setProfileData] = useState({
        name: "",
        birthCity: "",
        birthState: "",
        birthDate: "",
        birthTime: "",
    });

    const navigate = useNavigate();
    const user = auth.currentUser;

    const handleInputChange = (e) => {
        const { name, value } = e.target;
        setProfileData((prevState) => ({
            ...prevState,
            [name]: value,
        }));
    };

    const handleSubmit = async(e) => {
        e.preventDefault();

        //Make sure all fields are filled in
        if (!profileData.name || !profileData.birthCity ||profileData.birthState ||profileData.birthDate || profileData.birthTime) {
            alert("Please fill in all fields.");
            return;
        }

        try {
            //Update user doc in Firestore with their completed profile.
            const userRef = doc(db, "users", user.uid);
            await updateDoc(userRef, {
                name: profileData.name,
                birthCity: profileData.birthCity,
                birthState: profileData.birthState,
                birthDate: profileData.birthDate,
                birthTime: profileData.birthTime,
                profileComplete: true,

            });

            navigate('/home');
        } catch (error) {
            console.error("Error update profile: ", error);
        }
    }


    return (
        <div className = "input-profile">
            <form onSubmit = {handleSubmit}>
                <div>
                    <label htmlFor = "name">Name</label>
                    <input
                        type = "text"
                        id="name"
                        name="name"
                        value={profileData.name}
                        onChange={handleInputChange}
                        placeholder = "name"
                        required
                    />
                </div>
                <div>
                    <label htmlFor = "birthCity">birthCity</label>
                    <input
                        type = "text"
                        id="birthCity"
                        name="birtCity"
                        value={profileData.birthCity}
                        onChange={handleInputChange}
                        placeholder = "Berkeley"
                        required
                    />

                </div>
                <div>
                    <label htmlFor = "birthState">birthState</label>
                    <input
                        type = "text"
                        id="birthState"
                        name="birtState"
                        value={profileData.birthState}
                        onChange={handleInputChange}
                        placeholder = "CA"
                        required
                    />

                </div>

                <div>
                    <label htmlFor = "birthDate">birthDate</label>
                    <input
                        type = "text"
                        id="birthDate"
                        name="birtDate"
                        value={profileData.birthDate}
                        onChange={handleInputChange}
                        placeholder = "08/14/2003"
                        required
                    />

                </div>

                <div>
                    <label htmlFor = "birthTime">birthTime</label>
                    <input
                        type = "text"
                        id="birthTime"
                        name="birtTime"
                        value={profileData.birthTime}
                        onChange={handleInputChange}
                        placeholder = "3:47 PM"

                    />

                </div>
                <button type = "submit">Generate Chart</button>

            </form>
        </div>
    )



}

export default InputProfile;
