import { useEffect } from 'react';
import { getAuth, GoogleAuthProvider, EmailAuthProvider, onAuthStateChanged } from 'firebase/auth'; 
import firebase from "firebase/compat/app";
import * as firebaseui from "firebaseui";
import "firebaseui/dist/firebaseui.css";
import {app} from "../../firebase";
import {useNavigate} from "react-router-dom";

function Login() {

    useEffect(() => {
        //Initilize FirebaseUI Auth instance
        const ui = 
            firebaseui.auth.AuthUI.getInstance() ||
            new firebaseui.auth.AuthUI(getAuth(app));

    ui.start("#firebaseui-auth-container", {
        signInOptions: [
            {
                provider: GoogleAuthProvider.PROVIDER_ID,
                customParameters: {
                  prompt: "select_account",
                },
              },
            {
                provider: EmailAuthProvider.PROVIDER_ID
            }
        ],
        signInSuccessUrl: "/inputprofile",
    });
    },[]);


    //Check if user has logged in before
    const auth =getAuth(app);
    onAuthStateChanged(auth, (user) => {
        if (user) {
            //If the user exists, check if they've completed their profile w/ bday info
            const userRef = firebase.database().ref("users/" +user.uid);
            userRef.once("value", (snapshot) => {
                const userData = snapshot.val();
                if (userData && userData.profileComplete) {
                    //If profileComplete is true, then redeirct to User Home
                    navigate("/home");
                } else {
                    //If no profile exists or incomplete, redirect to Input Profile
                    navigate("")
                }
            })
        }
    })
    return (

    <div id ="firebaseui-auth-container"></div>
    );



}

export default Login
